(function(global){const PPB_TooltipsLibrary={init(){this.applyTooltips(document);this.bindGlobalClick()},applyTooltips(context=document){const tooltips=context.querySelectorAll('[data-toggle="ppbtooltip"].ppb-tooltips-library');tooltips.forEach(tooltip=>{if(tooltip.dataset.tooltipProcessed==="true")return;tooltip.dataset.tooltipProcessed="true";const tooltipBox=tooltip.querySelector(".tooltip-text");if(!tooltipBox)return;if(!tooltipBox.querySelector("i")){const arrow=document.createElement("i");tooltipBox.appendChild(arrow)}if(tooltip.classList.contains("click")){tooltip.addEventListener("click",event=>{if(!event.target.closest(".clickable")){event.preventDefault();event.stopPropagation()}tooltip.classList.toggle("is-active")})}})},bindGlobalClick(){document.addEventListener("click",function(event){const tooltip=event.target.closest('[data-toggle="ppbtooltip"].ppb-tooltips-library.click');if(tooltip){if(!event.target.closest(".clickable")){event.preventDefault();event.stopPropagation()}tooltip.classList.toggle("is-active")}})},refresh(context=document){this.applyTooltips(context)}};document.addEventListener("DOMContentLoaded",()=>PPB_TooltipsLibrary.init());global.PPB_TooltipsLibrary=PPB_TooltipsLibrary})(window);